mapper.select:
select l.username as username, l.type as type, l.starttime as starttime, l.endtime as endtime, l.whenlong as whenlong, l.myleave_id as myleaveId, l.reason as reason from oa_myleave l where 1=1
and ( l.reason LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or l.username LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and l.username = #{pd.username}
and l.type=#{pd.type}
order by l.endtime desc

select l.username as username, l.type as type, l.starttime as starttime, l.endtime as endtime, l.whenlong as whenlong, l.myleave_id as myleaveId, l.reason as reason from oa_myleave l where 1=1
and ( l.reason LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or l.username LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and l.username = #{pd.username}
and l.type=#{pd.type}
order by l.endtime desc

select l.username as "username", l.type as "type", l.starttime as "starttime", l.endtime as "endtime", l.whenlong as "whenlong", l.myleave_id as "myleaveId", l.reason as "reason" from oa_myleave l where 1=1
and ( l.reason LIKE '%' || #{pd.keywords} || '%' or l.username LIKE '%' || #{pd.keywords} || '%' )
and l.username = #{pd.username}
and l.type=#{pd.type}
order by l.endtime desc

